    //--------------------------------------------------------//

    let birthData = {item: this.state.text}
    this.addBirth(birthData)
  } 

  handleDelete = id => {
    console.log(id)
    ai.delete(`/delete/${id}`)
    .then((res)=>{
      this.showBirth()
    })
  }

