        this.setState({ name: e.target.value})
      }
      handleChangeImage = e => {
        this.setState({image: e.target.value})
      }

      handleChangeDate = e => {
        this.setState({birthDate: e.target.value})
      }


      handleAdd = e => {
        e.preventDefault();
