    baseURL:'http://127.0.0.1:8000/api'
})

class Plan extends Component {
//  const arr = props.p
state = {
    items: [],
    text: ""
  }
  showBirth = ()=>{
    ai.get('/list/')
    .then((res)=>{
      console.log(res.data)
        this.setState({items:res.data})
        console.log("name", res.data.name)
    })
  }
  componentDidMount(){
    this.showBirth();
  }

render(){
    return(

        <main id='site-main'>
        <div className="board">
