import React, { Component } from 'react'
import Plan from './MyComponents/Plan'
import "./components/plan.css"
import axios from 'axios'

//----------Axios Instance----------------//
const ai = axios.create({
    baseURL:'http://127.0.0.1:8000/api'
})

class App extends Component {
  state = {
    items: [],
    text: ""
  }

//Show the listed data from API
  showBirth = ()=>{
    ai.get('/list/')
    .then((res)=>{
      // console.log(res.data)
        this.setState({items:res.data})
    })
  }

  //Show the listed data from API
  addBirth = (data)=>{
    // axios.post('http://127.0.0.1:8000/create/', data)
    ai.post('/create/', data)
    .then((res)=>{
      // console.log(res.data)
        this.setState({text: ''})
        this.showBirth()
    })
  }


  handleChange = e => {
    this.setState({ text: e.target.value })
  }

  handleAdd = e => {

    //---------------Manually Added Data--------------------//
    // if (this.state.text !== "") {
    //   const item = [...this.state.items, this.state.text];
    //   this.setState({ items: item, text: "" });
    // }
    //--------------------------------------------------------//

    let birthData = {item: this.state.text}
    this.addBirth(birthData)
  } 

  handleDelete = id => {
    console.log(id)
    ai.delete(`/delete/${id}`)
    .then((res)=>{
      this.showBirth()
    })
    //---------------Manually deleted Data--------------------//
      //   console.log("Deleted", id);
      //   const Olditems = [...this.state.items]
      //   console.log("Olditems", Olditems);
      //   const NewItems = Olditems.filter((element, i) => {
      //     return i !== id
      //   })
      //   console.log("Newitems", NewItems);
      //   this.setState({ items: NewItems });
    //--------------------------------------------------------//
  }

  //---------------Show the list in console--------------------//
  componentDidMount(){
    this.showBirth();
  }

  render() {
    return (
      <div className="container-fluid my-5">
        <div className="row">
          <div className="col-sm-6 mx-auto text-white shadow-lg p-3">
            <h2 className="text-center"> Today's Plan </h2>
            <div className="container-fluid">
              <div className="row">
                <div className="col-9">
                  <input type="text" className="form-control" placeholder="Write Plan Here" value={this.state.text} onChange={this.handleChange} />
                </div>
                <div className="col-2">
                  <button className="btn btn-warning px-5 font-weight-bold" onClick={this.handleAdd}>Add</button>
                </div>
              </div>
              <div className="conatiner">
                <ul className="list-unstyled row m-5">

                  {
                    this.state.items.map((value, i) => {
                      //---------------Print in console--------------------// 
                      // {console.log(value.id, value.item, i)}

                      return <Plan key={i} id={value.id} value={value.item} sendData={this.handleDelete} />
                    })
                  }
                </ul>
              </div>
            </div>

          </div>
        </div>
      </div>
    )
  }
}

export default App;





















