import React from 'react';

export default function list(props) {
 const arr = props.info
 const bg = props.upcoming
 if (!arr) return;

 const bgColor = bg ? { backgroundColor : "#ffe66d"} : {};
  return (
    <ul>
     {
         arr.map( (person, index) => {

            return (
                <li key={index}>
                    <div className="flex" style={bgColor}>
                        <img src={person.image} alt="img" />
                        <div className="title">
                            <h3 className='name'>{person.name}</h3>
                            <h5 className="age">Birthday : {person.date}</h5>
                            <h5 className="age">{Old(person.date)} Year old</h5>
                        </div>
                    </div>
                </li>
            )
        })
     }
   </ul>
  );
}
function Old(personAge){
    let year = new Date(personAge).getFullYear();
    let currentYear = new Date().getFullYear();

    let age = currentYear - year;
   return age;
}
