import React from 'react'
import axios from 'axios'
const ai = axios.create({
    baseURL:'http://127.0.0.1:8000/api'
})

export default function list(props) {

    function showBirth (){
        ai.get('/list/')
        .then((res)=>{
          console.log("Data inside Plan:", res.data)
            this.setState({items:res.data})
            // console.log("data inside Plan name", res.data.name)
        })
      }     
      function handleDelete(id){
        console.log(id)
        ai.delete(`/delete/${id}`)
        .then((res)=>{showBirth()
        })
      }   
 const arr = props.info
 const bg = props.upcoming
 if (!arr) return;
//  if (arr==null) return "No birthday";

 const bgColor = bg ? { backgroundColor : "#ffe66d"} : {};
  return (
    <ul>
     {
         arr.map( (person, index) => {

            return (
                <li key={index}>
                    <div className="flex" style={bgColor}>
                        <img src={person.image} alt="img" />
                        <div className="title">
                            <h3 className='name'>{person.name}</h3>
                            <h5 className="age">Birthday : {person.date}</h5>
                            <h5 className="age">Id : {person.id}</h5>
                            <h5 className="age">{Old(person.date)} Year old</h5>
                        </div>
                        <button onClick={()=>handleDelete(person.id)}>Delete</button>
                    </div>
                </li>
            )
        })
     }
   </ul>
  );
}
function Old(personAge){
    var today = new Date();
    var birthDate = new Date(personAge);
    var age = today.getFullYear() - birthDate.getFullYear();
    var m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) 
    {
        age--;
    }
    return age;
}
