// import React from 'react';
import React, { useState } from 'react';

export default function AddTodo({addTodo}) {

const [title, setTitle] = useState("")   
const [desc, setDesc] = useState("")  
const [date,setDate] = useState("")  
// const submit = (e)=>{
//     e.preventDefault();
//     if(!title || !desc ||!date){
//         alert("Title or Description or date is empty")
//     }
//     else{
//         addTodo(title, desc);
//         setTitle("")
//         setDesc("")
//         setDate("")
//     }

// }
const handleAdd = (e)=> {
  e.preventDefault();
    if(!title || !desc ||!date){
        alert("Title or Description or date is empty")
    }
    else{
        addTodo(title, desc);
        setTitle("")
        setDesc("")
        setDate("")
    }
  let birthData = {item: this.state.text}
  this.addBirth(birthData)
} 


  return (
    <div className="container my-10">
     <div className="container text-center my-15 py-15">
         <h3>Add Birthdays</h3>
     </div>
        <form onSubmit={handleAdd}>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label">Todo title</label>
              <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name="title" value={title} 
              onChange={(e)=>{
                  setTitle(e.target.value)
              }}/>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputPassword1" className="form-label">Todo description</label>
              <input type="text" className="form-control" id="exampleInputPassword1" name="desc" value={desc}
                onChange={(e)=>{
                  setDesc(e.target.value)
              }} />
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputPassword1" className="form-label">Todo description</label>
              <input type="date" className="form-control" id="exampleInputPassword1" name="desc" value={date}
                onChange={(e)=>{
                  setDesc(e.target.value)
              }} />
            </div>

            <button type="submit" className="btn btn-primary" onClick={this.handleAdd}>Submit</button>
          </form>
    </div>

  );
}
