import React from 'react';

export default function list({props}) {
  return (
      <ul>
          {iterate(props)}
      </ul>
  );
}

function iterate(props){
    if (!props) return;
    const bgColor = props.upcoming ? { backgroundColor : "#ffe66d"} : {};
    return (
        <>
            <li key={props.id}>
                            <div className="flex" style={bgColor}>
                                <img src={props.image} alt="img" />
                                <div className="title">
                                    <h3 className='name'>{props.name}</h3>
                                    <h5 className="age">Birthday : {props.date}</h5>
                                    <h5 className="age">{Old(props.date)} years</h5>
                                </div>
                            </div>
                        </li>
        </>
    )
}

// how old the person is
function Old(personAge){
    let year = new Date(personAge).getFullYear();
    let currentYear = new Date().getFullYear();

    let age = currentYear - year;
   return age;
}
