import { Component } from "react"
import axios from 'axios'


const ai = axios.create({
    baseURL:'http://127.0.0.1:8000/api'
})
class BirthAdd extends Component{

    state = {
        items: [],
        text: ""
      } 

    addBirth = (data)=>{
        // axios.post('http://127.0.0.1:8000/create/', data)
        ai.post('/create/', data)
        .then((res)=>{
          // console.log(res.data)
            this.setState({text: ''})
            // this.showBirth()
        })
      }

      handleChange = e => {
        this.setState({ text: e.target.value })
      }

      handleAdd = e => {

        //---------------Manually Added Data--------------------//
        // if (this.state.text !== "") {
        //   const item = [...this.state.items, this.state.text];
        //   this.setState({ items: item, text: "" });
        // }
        //--------------------------------------------------------//

        let birthData = {item: this.state.text}
        this.addBirth(birthData)
      } 
      render(){
        return(
            <div className="container my-10">
            <div className="container text-center my-15 py-15">
                <h3>Add Todos</h3>
            </div>
               <form onSubmit={this.handleAdd}>
                   <div className="mb-3">
                     <label htmlFor="exampleInputEmail1" className="form-label">Todo title</label>
                     <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name="title" value={this.state.text} 
                     onChange={this.handleChange}/>
                   </div>
                   <div className="mb-3">
                     <label htmlFor="exampleInputPassword1" className="form-label">Todo description</label>
                     <input type="text" className="form-control" id="exampleInputPassword1" name="desc" value={this.state.text}
                    onChange={this.handleChange}
                     />
                   </div>
                   <button type="submit" className="btn btn-primary">Submit</button>
                 </form>
           </div>
        );
      }

}

export default BirthAdd
